using System;using System.Data.Entity;namespace TalatShop.Models{    public class ShopModel    {        public int ID { get; set; }        public string ItemName { get; set; }        public DateTime PurchaseDate { get; set; }        public string ItemType { get; set; }        public decimal ItemPrice { get; set; }    }    public class TalatShopDBContext : DbContext    {        public DbSet<ShopModel> Items { get; set; }    }}
